var mySpreadSheet = SpreadsheetApp.openById('1UMz4wuoneU_7ynfbQs_GLIuGkctK4baIj_QcnX7SvEg');
var mySheet = mySpreadSheet.getSheetByName('Cases');

function getSheetValues(){

  var sheetValues = mySheet.getSheetValues(2,2,4,3);

  var strSheetValues = sheetValues.join();
  return strSheetValues;

}

function getAllSheetValues(){

  var sheetLength = mySheet.getLastRow()-1;
  var sheetWidth = mySheet.getLastColumn();
  var sheetAllValues = mySheet.getSheetValues(2,1,sheetLength,sheetWidth);
  return sheetAllValues;
}

function getFirstValue(){

  var myValues = getAllSheetValues();
  var casesLength = myValues.length-1;
  var recordLength = myValues[0].length-1;
  var lastValue = myValues[casesLength][recordLength].toString();
  return lastValue;

}




////////////////////

function addRecordsFromSheetValues (){

    var relatedRecordsWLoop = [];
    var mySheetValues = getAllSheetValues();
    var recordLength = mySheetValues[0].length-1;

    for (var i = 0, len = mySheetValues.length; i < len; i++){

      var myCase = app.models.Cases.newRecord();

      var count = 0;
      myCase.POD = mySheetValues[i][count];
      myCase.CaseAge = mySheetValues[i][++count];
      myCase.CaseNumber = mySheetValues[i][++count];

      app.saveRecords([myCase]);

      var myEmpByKey = app.models.Employee.getRecord(mySheetValues[i][recordLength].toString());

      myCase.relEmployee = myEmpByKey;
      app.saveRecords([myCase]);

      relatedRecordsWLoop.push(myCase);

    }

  app.saveRecords(relatedRecordsWLoop);

}

